class SwiftManagedObject:
    """
    A base class for managing references to Swift objects in Python.

    Attributes
    ----------
    _address : int
        Memory address of the Swift object.
    """
                                
    def __init__(self, address: int):
        self._address = address


class SwiftManagedCollection(SwiftManagedObject):
    """
    A collection class that manages a list of Swift objects in Python.
    """

    def __iter__(self):
        for i in range(len(self)):
            yield self[i]
